CREATE DATABASE water;
USE water;

ALTER DATABASE water DEFAULT CHARACTER SET utf8 collate utf8_general_ci;

CREATE TABLE `location` (
    `idlocation` VARCHAR(45) NOT NULL PRIMARY KEY,
    `location1` VARCHAR(10) NOT NULL,
    `location2` VARCHAR(10) NOT NULL,
    `location3` VARCHAR(10) NOT NULL,
    `location4` VARCHAR(10) NOT NULL
) ENGINE=INNODB;

CREATE TABLE `criteria` (
    `idcriteria` VARCHAR(10) NOT NULL PRIMARY KEY,
    `maxval` DOUBLE,
    `minval` DOUBLE,
    `desc` VARCHAR(45) NOT NULL,
    `unit` VARCHAR(10)
) ENGINE=INNODB;

CREATE TABLE `datapoint` (
    `iddatapoint` INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
    `timestamp` VARCHAR(20) NOT NULL,
    `value` DOUBLE NOT NULL,
    `flag` BIT(1) NOT NULL,
    `idlocation` VARCHAR(45) NOT NULL,
    `idcriteria` VARCHAR(10) NOT NULL
) ENGINE=INNODB;

ALTER TABLE datapoint ADD FOREIGN KEY(idlocation) REFERENCES location (idlocation) on delete cascade on update cascade;
ALTER TABLE datapoint ADD FOREIGN KEY(idcriteria) REFERENCES criteria (idcriteria) on delete cascade on update cascade;




